import java.util.Scanner; public class SimpleChatbot { public static void main(String[] args) { Scanner scanner = new Scanner(System.in); System.out.println("Hello! I'm your chatbot. What can I do for you today?"); while (true) { String userMessage = scanner.nextLine(); String response = getBotResponse(userMessage); System.out.println("Bot: " + response); if (userMessage.equalsIgnoreCase("bye")) { System.out.println("Bot: Goodbye! Have a great day."); break; } } scanner.close(); } public static String getBotResponse(String userMessage) { userMessage = userMessage.toLowerCase(); if (userMessage.contains("hello") || userMessage.contains("hi")) { return "Hello! How can I assist you?"; } else if (userMessage.contains("how are you")) { return "I'm just a chatbot, but thanks for asking!"; } else if (userMessage.contains("bye")) { return "Goodbye! If you have more questions, feel free to return."; } else { return "I'm not sure how to respond to that. Please ask another question."; } } }